import React, { useState } from "react";
import { useEffect } from "react";
import { Button, Table, Modal } from "react-bootstrap";

function MyModal(props) {
  console.log(props.pokemon);

  return (
    <Modal
      {...props}
      size="lg"
      aria-labelledby="contained-modal-title-vcenter"
      centered
    >
      <Modal.Header closeButton>
        <Modal.Title id="contained-modal-title-vcenter">
          Edit Appointment
        </Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <h1>modal</h1>
      </Modal.Body>
    </Modal>
  );
}

function GrabData(page) {
  const [pokemons, setPokemon] = useState([]);
  const body = {
    page: page,
  };
  useEffect(() => {
    fetch("/api/pokemon/", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(body),
    })
      .then((res) => res.json())
      .then((pokemon) => setPokemon(pokemon));
  }, [page]);
  console.log(body);

  return pokemons;
}

function Films() {
  const [page, setPage] = useState(0);
  const [modalShow, setModalShow] = useState(false); //modal state
  const [infoUrl, setInfoUrl] = useState();
  let data = GrabData(page);
  let pokemons = data.results;

  // if (data.next != null) {
  //   console.log(data.next);
  // }
  // if (data.previous != null) {
  //   console.log(data.previous);
  // }
  //console.log(pokemons);
  function handleClickNext() {
    let newPage = page + 10;
    setPage(newPage);
    // GrabData(page);
  }
  function handleClickPrev() {
    let newPage = page - 10;
    setPage(newPage);
  }

  function moreInfo(url) {
    setInfoUrl(url);
    //console.log(url);
    setModalShow(true);
  }

  if (pokemons != null) {
    return (
      <div>
        <h1>hi</h1>
        <Button hidden={!data.previous} onClick={handleClickPrev}>
          previous
        </Button>
        <Button hidden={!data.next} onClick={handleClickNext}>
          next
        </Button>

        <Table striped bordered hover>
          <thead>
            <tr>
              <th>#</th>
              <th>Name</th>
              <th>Info</th>
            </tr>
          </thead>
          <tbody>
            {pokemons.map((pokemon) => (
              <tr key={pokemon.url}>
                <td>
                  {pokemon.url
                    .replace("https://pokeapi.co/api/v2/pokemon/", "")
                    .replace("/", "")}
                </td>
                <td>{pokemon.name}</td>
                <td>
                  <Button
                    onClick={() => {
                      moreInfo(pokemon.url);
                    }}
                  >
                    info
                  </Button>
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
        <MyModal
          show={modalShow}
          onHide={() => setModalShow(false)}
          pokemon={infoUrl}
        />
      </div>
    );
  } else return <h1>loading</h1>;
}

export default Films;
